package com.university.library.model;

import java.util.Date;

public class LoanAsset {

    // Autogenerated Unique Id (Primary Key)
    private String loanAssetId;
    private String assetId;
    private String userId;
    private Date loanDate;
    private Date returnDate;
    private Double fineAmount = 0.0;
    private Date renewDate;

    public LoanAsset(String loanAssetId , String assetId , String userId , Date loanDate , Date returnDate , Double fineAmount , Date renewDate) {
        this.loanAssetId = loanAssetId;
        this.assetId = assetId;
        this.userId = userId;
        this.loanDate = loanDate;
        this.returnDate = returnDate;
        this.fineAmount = fineAmount;
        this.renewDate = renewDate;
    }

    public LoanAsset() {
    }

    public String getLoanAssetId() {
        return loanAssetId;
    }

    public void setLoanAssetId(String loanAssetId) {
        this.loanAssetId = loanAssetId;
    }

    public String getAssetId() {
        return assetId;
    }

    public void setAssetId(String assetId) {
        this.assetId = assetId;
    }

    public String getUserId() {
        return userId;
    }

    public void setUserId(String userId) {
        this.userId = userId;
    }

    public Date getLoanDate() {
        return loanDate;
    }

    public void setLoanDate(Date loanDate) {
        this.loanDate = loanDate;
    }

    public Date getReturnDate() {
        return returnDate;
    }

    public void setReturnDate(Date returnDate) {
        this.returnDate = returnDate;
    }

    public Double getFineAmount() {
        return fineAmount;
    }

    public void setFineAmount(Double fineAmount) {
        this.fineAmount = fineAmount;
    }

    public Date getRenewDate() {
        return renewDate;
    }

    public void setRenewDate(Date renewDate) {
        this.renewDate = renewDate;
    }

    @Override
    public String toString() {
        return "loanDate : " + loanDate + '\n' +
                "returnDate : " + returnDate + '\n' +
                "fineAmount : " + fineAmount + '\n' +
                "renewDate : " + renewDate;
    }
}